mapper.select:
	select 
 	
 	from user
 	
 		
  		
  			and id = #{condition.id,javaType=INTEGER}
  		
  		
  			and name = #{condition.name,javaType=STRING}
  		
  		
  			and age = #{condition.age,javaType=INTEGER}
  		
  		
  			and email = #{condition.email,javaType=STRING}

	select 
 	count(1)
 	from user
 	
 		
  		
  			and id = #{condition.id,javaType=INTEGER}
  		
  		
  			and name = #{condition.name,javaType=STRING}
  		
  		
  			and age = #{condition.age,javaType=INTEGER}
  		
  		
  			and email = #{condition.email,javaType=STRING}

	select 
 	
 	from user
 	
 		
  		
  			and id = #{condition.id,javaType=INTEGER}
  		
  		
  			and name = #{condition.name,javaType=STRING}
  		
  		
  			and age = #{condition.age,javaType=INTEGER}
  		
  		
  			and email = #{condition.email,javaType=STRING}
  		
  	
 	
  ${orderby}
  limit #{start },#{limit }

